package com.tcl.john.dialog.utils;import android.databinding.BindingAdapter;import android.databinding.BindingConversion;import android.graphics.Typeface;import android.widget.TextView;public class FontUtil {    @BindingConversion    public static Typeface convertStringToFace(String fontName) {        try {            return Typeface.create(fontName, 0);        } catch (Exception e) {            return null;        }    }    @BindingAdapter("android:typeface")    public static void setTypeface(TextView v, String s) {        v.setTypeface(convertStringToFace(s));    }}