package com.tcl.john.dialog.utils;import android.content.Context;import android.view.WindowManager;import com.tcl.john.dialog.widget.EditDialog;public class WidgetUtil {    /**     * 创建带编辑框的Dialog     *     * @param context     * @param resId       Dialog style , 如：R.style.CustomDialog     * @param titleId     * @param contentId     * @param leftBtnId     * @param rightBtnId     * @param systemAlert 是否全局弹出     * @return     */    public static EditDialog createEditDialog(Context context, int resId, int titleId,                                              int contentId, int leftBtnId, int rightBtnId, boolean systemAlert) {        EditDialog editDialog = new EditDialog(context, resId,                context.getString(titleId),                context.getString(contentId));        editDialog.setPositiveBtn(context.getString(leftBtnId));        editDialog.setNegativeBtn(context.getString(rightBtnId));        if (systemAlert && editDialog.getWindow() != null) {            editDialog.getWindow().setType(WindowManager.LayoutParams.TYPE_SYSTEM_ALERT);        }        return editDialog;    }}